import React, { Component } from 'react'
import {NavLink ,Route} from 'react-router-dom'
import Detail from './Detail'
export default class Message extends Component {
      state={
        messageArr:[
          {id:'01',title:'aaa'},
          {id:'02'},
          {id:'03'}
        ]
      }
  
  
  render() {
    return (
      <div>
      <ul>
      <li>

    {/**也可以用map遍历message生成link，传params直接 messageObj.id */}
      
    {/**想路由组件传params参数（不用''用``）   传到接受组件的props.match.params中 */}
        <NavLink to={`/home/message/detail/${this.state.messageArr[0].id}`}>message001</NavLink>&nbsp;&nbsp;
      </li>
      <li>
        <NavLink to={`/home/message/detail/${this.state.messageArr[1].id}`}>message002</NavLink>&nbsp;&nbsp;
      </li>
      <li>
        <NavLink to={`/home/message/detail/${this.state.messageArr[2].id}`}>message003</NavLink>&nbsp;&nbsp;
      </li>
    </ul>
    {/*用params时注册路由要设置接受参数 */}
   <Route path={'/home/message/detail/:id'} component={Detail}/ > 
   

    {/*用search传递参数 (要在接受组件中引入querystring)  传到接受组件的props.location.search 中
     <NavLink to={`/home/message/detail/?id=${this.state.messageArr[0].id}$title={this.state.messageArr[0].title} `}>message001</NavLink>&nbsp;&nbsp;
      设置路由不再需要接受数据
     <Route path={'/home/message/detail'} component={Detail}/ > 
     */}
   
    {/*用state传递参数    要将to={}写成一个对象  传到接受组件的props.location.state 中 
     <NavLink to={ {pathname:'/home/message/detail',state:{id:this.state.messageArr[0].id,title:this.state.messageArr[0].title}} }>message001</NavLink>&nbsp;&nbsp;
      设置路由不再需要接受数据
     <Route path={'/home/message/detail} component={Detail} />  
     */}
   
   
    </div>

    )
  }
}
