import React from 'react';
import "./App.css"
import Header from './components/Header'
import List from './components/List'
//import Item from './components/Item'
import Footer from './components/Footer'


class App extends React.Component {
    state={
        todos:[
            {id:'1',name:'学语文',done:false},
            {id:'2',name:'学数学',done:false},
            {id:'3',name:'学英语',done:false},
            {id:'4',name:'学捏马',done:true}
           ]}
      todos=this.state.todos
     add=(item)=>{
        this.setState=(todos[(todos.length)+1]:{})
     }
    render() {
     return <div>
             <div className="todo-container">
                <div className="todo-wrap">
                <Header add={this.add}  todos={this.state.todos} />     
                <List todos={this.state.todos}></List>
                <Footer todos={this.state.todos}></Footer>               
                </div>
               </div>
        </div>;
    }
}
export default App;
