import React, { Component } from 'react'
import Message from "./pages/Message"
import News from "./pages/News"
import {NavLink, Route ,Switch,Redirect} from 'react-router-dom'
export default class Home extends Component {
  render() {
    return (

      <div>
      <h2>home组件内容</h2>
      <ul class="nav nav-tabs">
      <li>
        <NavLink className="list-group-item active" to="/home/news">News</NavLink>
      </li>
      <li>
        <NavLink  active className="list-group-item " to="/home/message">Message</NavLink>
      </li>
    </ul>





      
      <Switch>  {/*switch 的作用是当匹配到一个路径正确 的路由时结束匹配，提高效率，不在往下寻找 */}
      <Route path={'/home/message'} component={Message}/>
       <Route path={'/home/news'} component={News}/>
       <Redirect to={'/home/message'}></Redirect>   {/**当路由不是以上所有路由中的一个时，用redirect设置兜底路由地址 */}
      </Switch>
      </div>
    )
  }
}
