import React from 'react';
import './index.css'


export default class Footer extends React.Component {
  //全选按钮
  handleAll=(e)=>{
       this.props.handleAll(e.target.checked)
       console.log("handleAll  "+e.target.checked)
  }

 
  render() {
    const {todos}=this.props
    const doneCount=todos.reduce((pre,curitem)=>{
       return pre+ curitem.done?1:0 } ,0)
  
      return (
            <div className="todo-footer">
            <label>
              <input type="checkbox" unchecked  onChange={this.handleAll}/>
            </label>
            <span>
              <span>已完成{doneCount}</span> / 全部{this.props.todos.length}
            </span>
            <button className="btn btn-danger">清除已完成任务</button>
          </div>
        )
    }
}
