import React, { Component } from 'react'
import {NavLink ,Route} from 'react-router-dom'
import Detail from './Detail'
export default class Message extends Component {
      state={
        messageArr:[
          {id:'01'},
          {id:'02'},
          {id:'03'}
        ]
      }
  
  
  render() {
    return (
      <div>
      <ul>
      <li>
    {/**想路由组件传params参数，   传到接受组件的props.match.params中 */}
        <NavLink to={`/home/message/detail/${this.state.messageArr[0].id}`}>message001</NavLink>&nbsp;&nbsp;
      </li>
      <li>
        <NavLink to={`/home/message/detail/${this.state.messageArr[1].id}`}>message002</NavLink>&nbsp;&nbsp;
      </li>
      <li>
        <NavLink to={`/home/message/detail/${this.state.messageArr[2].id}`}>message003</NavLink>&nbsp;&nbsp;
      </li>
    </ul>
   <Route path={'/home/message/detail/:id'} component={Detail} ></Route>
    </div>
    )
  }
}
