import React, { Component } from 'react'
import {Link ,Route} from 'react-router-dom'
import Detail from './Detail'
export default class Message extends Component {
      state={
              messageArr:[
                {id:'01',title:'aaa'},
                {id:'02',title:'bbb'},
                {id:'03',title:'ccc'}
              ]
            }
   
        //编程式路由，用push方式跳转的函数
        pushshow=(id,title)=>{
          return()=>{
              //用params传递数据  push方式跳转按钮
              this.props.history.push(`/home/message/detail/${id}/${title}`)
              
              // 用 search 传递数据  push方式跳转按钮
                // this.props.history.replace(`/home/message/detail/${id}/${title}`) 
                
               //用state传递数据   push方式跳转按钮
              //  this.props.history.replace(`/home/message/detail/${id}/${title}`,{id,title})

            }
        } 


        //编程式路由，用replace方式跳转的函数
        replaceshow=(id,title)=>{
          return()=>{
            //用params传递数据
            this.props.history.replace(`/home/message/detail/${id}/${title}`) 

             // 用 search 传递数据 replace方式跳转按钮
               // this.props.history.replace(`/home/message/detail/${id}/${title}`) 
                
               //用state传递数据   replace方式跳转按钮
              //  this.props.history.replace(`/home/message/detail/${id}/${title}`,{id,title})
        }
        } 



  render() {
    const arr=this.state.messageArr
    
    return (
      <div>
      <ul>
        {/**也可以用map遍历message生成link，传params直接 messageObj.id */}
      
        {/**想路由组件传params参数（不用 ' ' 用 ` ` ）   传到接受组件的props.match.params中 */}
      <li>
        < Link to={`/home/message/detail/${arr[0].id}`}>message001</Link>&nbsp;&nbsp;
        <button onClick={this.pushshow(arr[0].id,arr[0].title)}>用push跳转</button>
        <button onClick={this.replaceshow(arr[0].id,arr[0].title)}>用replace跳转</button>
        </li>
      <li>
        < Link to={`/home/message/detail/${arr[1].id}`}>message002</ Link>&nbsp;&nbsp;
        
        <button onClick={this.pushshow(arr[1].id,arr[1].title)}>用push跳转</button>
        <button onClick={this.replaceshow(arr[1].id,arr[1].title)}>用replace跳转</button>
        </li> 
      <li>
        < Link to={`/home/message/detail/${arr[2].id}`}>message003</ Link>&nbsp;&nbsp;
       
       <button onClick={this.pushshow(arr[2].id,arr[2].title)}>用push跳转</button>
        <button onClick={this.replaceshow(arr[2].id,arr[2].title)}>用replace跳转</button>
      </li>
    </ul>


    {/*用params时注册路由要设置接受参数
      备注： 需要用/xx/xx传递数据   设置路由时需要设置  接受时从props.params
     */}
   <Route path={'/home/message/detail/:id/:title'} component={Detail}/ > 
   

    {/*用search传递参数 (要在接受组件中引入querystring)  传到接受组件的props.location.search 中
      备注：传递时用?和&  不需要接受，获取时要用qs，
      <NavLink to={`/home/message/detail/?id=${arr[0].id}$title={arr[0].title} `}>message001</NavLink>&nbsp;&nbsp;
      设置路由不再需要接受数据
     <Route path={'/home/message/detail'} component={Detail}/ > 
     */}
   
    {/*用state传递参数   地址栏不体现变化  刷新也会留住参数，因为BrowserRouter的props.history也有location会缓存
       备注： 用对象传递清晰，地址栏不可见  设置路由并不需要接受数据
      要将to={}写成一个对象  传到接受组件的props.location.state 中 
     <NavLink to={ {pathname:'/home/message/detail',state:{id:arr[0].id,title:arr[0].title}} }>message001</NavLink>&nbsp;&nbsp;
      设置路由不再需要接受数据
     <Route path={'/home/message/detail} component={Detail} />  
     */}
  
    </div>

    )
  }
}
