import React from 'react';
import './index.css'


export default class Footer extends React.Component {
  //全选按钮
  handleAll=(e)=>{
       this.props.handleAll(e.target.checked)
  }
clearAllDone=()=>{
  this.props.killAllDone()
  console.log("完成")
}
 
  render() {
    const {todos}=this.props
    let doneCount=todos.reduce((pre,curitem)=>{
       return pre+ (curitem.done?1:0 )} ,0)
  console.log(doneCount,todos.length)
      return (
            <div className="todo-footer">
            <label>
              <input type="checkbox" checked={doneCount===todos.length&& doneCount ?true:false}  onChange={this.handleAll}/>
            </label>
            <span>
              <span>已完成{doneCount}</span> / 全部{todos.length}
            </span>
            <button className="btn btn-danger" onClick={this.clearAllDone}>清除已完成任务</button>
          </div>
        )
    }
}
