import React, { Component } from 'react'
import axios from 'axios'



export default class Search extends Component {
  
  search=()=>{
    const name=this.name.value;
   
    //发送请求马上该isfierst 和loading   
    this.props.update({isFirst:false,loading:true,})

    axios.get('https://api.github.com/searcha/users?q='+name).then(
      res=>{
          //发完该loading
          this.props.update({users:res.data.items,loading:true});
          console.log("axios"+res.data.items);
          console.log("done!")},
      
          err=>{
           this.props.update({err:err.message,loading:true})
      }
    )
   
  }

  render() {
    return (
     <div> <section className="jumbotron">
     <h3 className="jumbotron-heading">Search Github Users</h3>
     <div>
       <input ref={c =>this.name=c } type="text" placeholder="enter the name you search"/>&nbsp;
       <button onClick={this.search}>Search</button>
     </div>
   </section></div>
    )
  }
}
