import React from 'react';
import Header from './components/Header'
import List from './components/List'
import Footer from './components/Footer'
import "./App.css"

export default class App extends React.Component {
    state={todos:[ 
                    {id:'1',name:'学语文',done:false},
                    {id:'2',name:'学数学',done:false},
                    {id:'3',name:'学英语',done:false},
                    {id:'4',name:'学捏马',done:true}
                 ]}
    
    render() {
         return(
            <div className="todo-container">
            <div className="todo-wrap">
                <Header  todos={this.state.todos} add={this.add}  />     
                <List    todos={this.state.todos} checkSingle={this.checkSingle}></List>
                <Footer  todos={this.state.todos}></Footer>               
            </div>
             </div>
               ) 
            }
   //增加新的代办项  
       add=(newitem)=>{
        const  newlist=[newitem,...this.state.todos]
        this.setState({todos:newlist})   
        console.log("add")
         }
       
   //改变单选框的done值
       checkSingle=(id,done)=>{
           const {todos}=this.state
           const newtodos = todos.map((item)=>{
             if(item.id===id)
              return {...item,done}
              else
              return item
           })
            this.setState=({todos:newtodos})
            
       }
}
