/* eslint-disable no-lone-blocks */
import React, { Component } from 'react'
//假装是服务器返回的数据
const data=[
    {id:'01',title:'qqq',sad:'dadas'},
    {id:'02',title:'qwww',sad:'daaaas'},
    {id:'03',title:'www',sad:'dssss'}
 ]
 
 


export default class Detail extends Component {
  forward=()=>{
    this.props.history.goForward()
  }
  back=()=>{
    this.props.history.goBack()
  }  
    render() {
       {/*   params接收参数
           在传来的数据中获取所选message的id*/}
        const {id,title}=this.props.match.params
        
       {/*  用search接受参数     
          前提是   import qs from  'querystring'   qs的两个方法parse,stringfy
           const {search}=this.props.location
           const {id,title}=qs.parse(search.slice(1))    用slice除去第一个？
      */}
       
      {/*  用state接受参数        
           const {id,title}=this.props.location.state  ||{}  
           const result=data.find(message=>{
              return message.id===id
              })  ||{}     如果没传state则将id title  result 都为空
      */}
       

            {/*在数据库中找到与其对应的id的对象，赋值的result中，以便后续调用*/}
            const result=data.find(message=>{
              return message.id===id
              })


    return (
      
      <div>
      <ul>
          <li>id:   {id}</li>
          <li>title:{ title}  </li>
          <li>sad: {result.sad}   </li>
      </ul>

        <button onClick={this.forward}>往前</button>
        <button onClick={this.back}>往后</button>
      </div>
    )
  }
}
