import React from 'react';
import './index.css'


export default class Item extends React.Component {
   state={mouse:false  }
 
   //鼠标悬浮时的样式改变index接布尔值
  handlemouse=(index)=>{
       return ()=>{
           this.setState({mouse:index})
       } 
   }
   //删除代办项
   deleteItem=(id)=>{
    if( confirm ('你正在删除一个事件')){
    return ()=>{
       
       this.props.deletetodo(id)

     }
    }
   }
   
   //勾选框点击改变状态函数
   isDone=(id)=>{
     const{checkSingle}=this.props
      return (e)=>{
        console.log(id,e.target.checked)
       checkSingle(id,e.target.checked)
      }
   }

  
    render() {
      const {id,name,done}=this.props
      return (
    
           <li style={{backgroundColor: this.state.mouse ? '#999' :'white' }} 
                        onMouseEnter={this.handlemouse(true)} 
                        onMouseLeave={this.handlemouse(false)} >
             
            <label>
              <input type="checkbox" checked={done}  onChange={this.isDone(id)}  />
              <span> {name} </span>
            </label>
            <button  onClick={this.deleteItem(id)}  className="btn btn-danger" 
                      style={{display: this.state.mouse ? 'block' :'none'}}>删除</button>
          </li>
        )
    }
}
