import React from 'react';
import './index.css'


class Header extends React.Component {
      state={additem:''}
     //增加代办项,并用app中的add修改state
      additem= (e)=>{
         if(e.keyCode===13 &&e.target.value) { 
             let newItem={id:this.props.todos.length+1,name:e.target.value,done:false}
             this.props.add(newItem)
             console.log("回车"+ newItem)  

             e.target.value=""
            }
         }
    
        render() {        
        return (
            <div className="todo-header">
            <input onKeyUp={this.additem} placeholder="请输入你的任务名称，按回车键确认"/>
            </div>
        )
    }
}


export default Header;
