import React from 'react';
import './index.css'


class  Item extends React.Component {
   state={mouse:false  }
 
  handlemouse=(index)=>{
       return ()=>{
           this.setState({mouse:index})
       } 
   }

   delete=(e)=>{
        e.style={display:'hidden'}}
   
    render() {
      const {name,done}=this.props
      return (
    
           <li style={{backgroundColor: this.state.mouse ? 'gray' :'white' }} onMouseEnter={this.handlemouse(true)}  onMouseLeave={this.handlemouse(false)} >
             
            <label>
              <input type="checkbox"  defaultChecked={done}/>
              <span>{name} </span>
            </label>
            <button  onClick={this.delete}  className="btn btn-danger" style={{display: this.state.mouse ? 'block' :'none'}}>删除</button>
          </li>
        )
    }
}


export default Item;
