import React from 'react';
import './index.css'


class Header extends React.Component {
      state={additem:''}
       demo= (e)=>{
        if(e.keyCode===13 &&e.target.value)
        {    e.target.value=""
            console.log(e.target.value)
            const addobj={id:this.props.todos.length+1,name:e.target.value,done:false}
            this.props.add(addobj)
        }
   }
    render() {
        
        return (
            <div className="todo-header">
            <input type="text"  onKeyUp={this.demo} placeholder="请输入你的任务名称，按回车键确认"/>
            </div>
        )
    }
}


export default Header;
